settings.custom_liquid:
{% assign product_sheet_object = product.metafields.custom.product_sheet.value %}
{% assign technical_specs_object = product.metafields.custom.technical_specs.value %}
{% assign installation_instructions_object = product.metafields.custom.installation_instructions.value %}
{% unless product_sheet_object == blank and technical_specs_object == blank and technical_specs_object == blank %}
<div class = "product-documents">
    {% assign product_sheet_url = product_sheet_object.url %}
    {% assign product_sheet_with_shopify_extension = product_sheet_url | split: '/' | last %}
    {% assign product_sheet_name = product_sheet_with_shopify_extension | split: '?' | first %}

    {% assign technical_specs_url = technical_specs_object.url %}
    {% assign technical_specs_with_shopify_extension = technical_specs_url | split: '/' | last %}
    {% assign technical_specs_name = technical_specs_with_shopify_extension | split: '?' | first %}

    {% assign installation_instructions_url = installation_instructions_object.url %}
    {% assign installation_instructions_with_shopify_extension = installation_instructions_url | split: '/' | last %}
    {% assign installation_instructions_name = installation_instructions_with_shopify_extension | split: '?' | first %}
    <ul>
        {% if product_sheet_object != blank %} 
        <li> 
            <a href = "{{product_sheet_url}}" target="_blank">
                <div class = "product-sheet-name">
                    {% if request.locale.iso_code == 'en' %}
                        <u>Product_Sheet</u>
                    {% else %}
                        <u>Fiche_Produit</u>
                    {% endif %}
                </div>
            </a> 
        </li>
        {% endif %}
        {% if technical_specs_object != blank %}
        <li>
            <a href = "{{technical_specs_url}}" target="_blank">
                <div class = "technical-specs-name">
                    {% if request.locale.iso_code == 'en' %}
                        <u>Technical_Specs</u>
                    {% else %}
                        <u>Sp√©cifications_Techniques</u>
                    {% endif %}
                </div>
            </a> 
        </li>
        {% endif %}
        {% if installation_instructions_object != blank %}
        <li>
            <a href = "{{installation_instructions_url}}" target="_blank">
                <div class = "installation-instructions-name">
                    {% if request.locale.iso_code == 'en' %}
                        <u>Installation Instructions</u>
                    {% else %}
                        <u>Instructions d'Installation</u>
                    {% endif %}
                </div>
            </a> 
        </li>
        {% endif %}
    </ul>
</div>
{% endunless %}
